import React from "react";
import "./styles.scss";
export const BarGraph = ({ data }) => {
  // Calculate the maximum value in the data array
  let maxValue = 0;
  for (let i = 0; i < data.length; i++) {
    maxValue = Math.max(maxValue, data[i][1]);
  }
  console.log(maxValue, data);
  return (
    <div className="bar-graph">
      {data.map((value, index) => {
        return (
          <>
            {console.log(value, (value[0] / maxValue) * 100)}
            <div
              className="bar"
              key={index}
              style={{
                height: `${(value[0] / maxValue) * 100}%`,
              }}
            />
          </>
        );
      })}
    </div>
  );
};
