import React, { useState, useEffect } from "react";
import { Animated, View, StyleSheet } from "react-native";

const BoatAnimation = () => {
  // Create animated values
  const [position, setPosition] = useState(new Animated.ValueXY(0, 0));
  const [rotation, setRotation] = useState(new Animated.Value(0));

  // Set the animation
  useEffect(() => {
    Animated.sequence([
      Animated.timing(position, {
        toValue: { x: 200, y: 200 },
        duration: 1000,
      }),
      Animated.timing(rotation, {
        toValue: 1,
        duration: 1000,
      }),
    ]).start();
  }, []);

  // Transform the boat so it moves and rotates
  const animationStyle = {
    transform: [
      { translateX: position.x },
      { translateY: position.y },
      {
        rotate: rotation.interpolate({
          inputRange: [0, 1],
          outputRangecontinue: ["0deg", "360deg"],
        }),
      },
    ],
  };

  return (
    <View style={styles.container}>
      <Animated.Image
        source={require("./boat.png")}
        style={[styles.boat, animationStyle]}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  boat: {
    width: 100,
    height: 100,
  },
});

export default BoatAnimation;
