import React from "react";

export const BarGraph = ({ data }) => {
  // Calculate the maximum value in the data array
  const maxValue = Math.max(...data);

  return (
    <div className="bar-graph">
      {data.map((value, index) => (
        <div
          className="bar"
          key={index}
          style={{
            height: `${(value / maxValue) * 100}%`,
          }}
        />
      ))}
    </div>
  );
};
