import React, { useState, useEffect } from "react";
import ship from "../../assets/ship.jpg";
export const BoatAnimation = () => {
  const [position, setPosition] = useState(0);
  const [boatDirection, setBoatDirection] = useState(1);

  useEffect(() => {
    const interval = setInterval(() => {
      let newPosition = position + boatDirection;
      if (newPosition >= 100 || newPosition <= 0) {
        setBoatDirection(boatDirection * -1);
      } else {
        setPosition(newPosition);
      }
    }, 25);
    return () => clearInterval(interval);
  }, [position, boatDirection]);

  return (
    <div>
      <div
        style={{
          position: "relative",
          height: "250px",
          width: "250px",
          overflow: "hidden",
        }}
      >
        <img
          src={ship}
          style={{
            position: "absolute",
            top: 0,
            left: position + "%",
          }}
          width="250px"
          height="250px"
          alt="seaphoto"
        />
      </div>
    </div>
  );
};
