import React, { useState, useEffect } from "react";
import ship from "../../assets/ship.png";
export const BoatAnimation = () => {
  const [position, setPosition] = useState(0);
  const [boatDirection, setBoatDirection] = useState(1); // set boatDirection to 1 for forward direction

  useEffect(() => {
    const interval = setInterval(() => {
      let newPosition = position + boatDirection;
      if (newPosition >= 100) {
        // check if newPosition exceeds 100
        setBoatDirection(0); // stop the boat from moving forward
      } else {
        setPosition(newPosition);
      }
    }, 25);
    return () => clearInterval(interval);
  }, [position, boatDirection]);

  return (
    <img
      src={ship}
      style={{
        position: "absolute",
        top: 0,
        left: position + "%",
        borderRadius: "50%",
      }}
      width="250px"
      height="250px"
      alt="seaphoto"
    />
  );
};
